import React, { useState } from 'react';
import { generatePDF } from '../api';
import '../css/SiparisForm.css';

const SiparisForm = () => {
  const [formData, setFormData] = useState({
    // Sipariş specific fields
    'SİPARİŞ GÜNÜ': '',
    'SİPARİŞ NUMARASI': '',
    'TEDARİKÇİ NUMARASI': '',

    // Responsible Person
    'RESPONSIBLE PERSON': '',
    'TELEPHONE': '',
    'EMAIL': '',

    // Recipient
    'RECIPIENT Şirket Adı': '',
    'RECIPIENT Adres': '',
    'RECIPIENT İlçe İl Ülke': '',
    'RECIPIENT Vat': '',
    'RECIPIENT Sorumlu Kişi': '',
    'RECIPIENT Telefon': '',
    'RECIPIENT Email': '',

    // Delivery Address
    'DELIVERY ADDRESS Şirket Adı': '',
    'DELIVERY ADDRESS Adres': '',
    'DELIVERY ADDRESS İlçe İl Ülke': '',
    'DELIVERY ADDRESS Vat': '',
    'DELIVERY ADDRESS Sorumlu Kişi': '',
    'DELIVERY ADDRESS Telefon': '',
    'DELIVERY ADDRESS Email': '',

    // Payment & Shipping Details
    'Payment Terms': '',
    'Transport Type': '',

    // Notes
    'Notlar': '',
  });

  // Sipariş ürün listesi - ekstra alanlarla
  const [goods, setGoods] = useState([
    {
      id: 1,
      'ARTIKEL NUMARASI': '',
      'GRAMAJ / EN': '',
      'KOMPOZISYON': '',
      'SEZON': '',
      'TERMIN ISLEM': '',
      'ADET (METRE)': '',
      'FIYAT': '',
      'AMOUNT': ''
    }
  ]);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleInputChange = (name, value) => {
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Banka bilgilerini getir
  const getBankInfo = (currency) => {
    const bankInfos = {
      'TRY': `BANKA : TEB (TÜRKIYE EKONOMI BANKASI )
ŞUBE: MERTER(032)
HESAP NO: 962246
IBAN :TR78 0003 2000 0320 0000 9622 46`,
      'USD': `BANKA : TEB (TÜRKIYE EKONOMI BANKASI )
BRANCH: MERTER(032)
ACCOUNT NO: 967978
SWİFT: TEBUTRIS 032
IBAN :TR29 0003 2000 0320 0000 9679 78`,
      'EUR': `BANKA : TEB (TÜRKIYE EKONOMI BANKASI )
BRANCH: MERTER(032)
ACCOUNT NO: 967979
SWİFT: TEBUTRIS 032
IBAN :TR29 0003 2000 0320 0000 9679 79`
    };
    return bankInfos[currency] || '';
  };

  // Ürün verilerini güncelleme
  const handleGoodsChange = (id, field, value) => {
    setGoods(prev => prev.map(item => {
      if (item.id === id) {
        const updatedItem = { ...item, [field]: value };

        // ADET (METRE) veya FIYAT değiştiğinde AMOUNT'u otomatik hesapla
        if (field === 'ADET (METRE)' || field === 'FIYAT') {
          const quantity = parseFloat(field === 'ADET (METRE)' ? value : updatedItem['ADET (METRE)']) || 0;
          const price = parseFloat(field === 'FIYAT' ? value : updatedItem['FIYAT']) || 0;
          updatedItem['AMOUNT'] = (quantity * price).toFixed(2);
        }

        return updatedItem;
      }
      return item;
    }));
  };

  // Yeni ürün ekleme
  const addGoods = () => {
    const newId = Math.max(...goods.map(g => g.id)) + 1;
    setGoods(prev => [...prev, {
      id: newId,
      'ARTIKEL NUMARASI': '',
      'GRAMAJ / EN': '',
      'KOMPOZISYON': '',
      'SEZON': '',
      'TERMIN ISLEM': '',
      'ADET (METRE)': '',
      'FIYAT': '',
      'AMOUNT': ''
    }]);
  };

  // Ürün silme
  const removeGoods = (id) => {
    if (goods.length > 1) {
      setGoods(prev => prev.filter(item => item.id !== id));
    }
  };

  // Toplam tutar hesaplama
  const calculateTotal = () => {
    return goods.reduce((total, item) => {
      return total + (parseFloat(item['AMOUNT']) || 0);
    }, 0).toFixed(2);
  };

  // Form gönderme
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // Para birimi tespiti (FIYAT alanından)
      const samplePrice = goods[0]?.['FIYAT'] || '';
      let currency = 'TRY';
      if (samplePrice.includes('USD') || samplePrice.includes('$')) {
        currency = 'USD';
      } else if (samplePrice.includes('EUR') || samplePrice.includes('€')) {
        currency = 'EUR';
      }

      // Banka bilgilerini otomatik doldur
      const updatedFormData = {
        ...formData,
        'Banka Bilgileri': getBankInfo(currency)
      };

      const requestData = {
        formData: updatedFormData,
        goods: goods,
        totals: {
          subtotal: calculateTotal(),
          vatRate: formData['KDV Ekle Enabled'] ? formData['KDV'] : '0',
          total: calculateTotalWithVAT()
        }
      };

      const response = await generatePDF(requestData, 'siparis');

      if (response.success) {
        setSuccess('Sipariş formu başarıyla oluşturuldu!');
        // PDF dosyasını indir
        if (response.pdfUrl) {
          window.open(response.pdfUrl, '_blank');
        }
      } else {
        setError(response.message || 'PDF oluşturulurken bir hata oluştu.');
      }
    } catch (err) {
      console.error('Form submission error:', err);
      setError('Sipariş formu gönderilirken bir hata oluştu.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="siparis-form-container">
      <div className="siparis-form-header">
        <h2>SİPARİŞ FORMU</h2>
        <p>Sipariş bilgilerini doldurun ve PDF olarak oluşturun</p>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          {success}
        </div>
      )}

      <form onSubmit={handleSubmit} className="siparis-form">
        {/* Sipariş Bilgileri */}
        <div className="form-section">
          <h3 className="section-title">Sipariş Bilgileri</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">SİPARİŞ GÜNÜ</label>
              <input
                type="date"
                className="form-input"
                value={formData['SİPARİŞ GÜNÜ']}
                onChange={(e) => handleInputChange('SİPARİŞ GÜNÜ', e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">SİPARİŞ NUMARASI</label>
              <input
                type="text"
                className="form-input"
                value={formData['SİPARİŞ NUMARASI']}
                onChange={(e) => handleInputChange('SİPARİŞ NUMARASI', e.target.value)}
                placeholder="Sipariş numarasını girin"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">TEDARİKÇİ NUMARASI</label>
              <input
                type="text"
                className="form-input"
                value={formData['TEDARİKÇİ NUMARASI']}
                onChange={(e) => handleInputChange('TEDARİKÇİ NUMARASI', e.target.value)}
                placeholder="Tedarikçi numarasını girin"
                required
              />
            </div>
          </div>
        </div>

        {/* Responsible Person */}
        <div className="form-section">
          <h3 className="section-title">Responsible Person</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">RESPONSIBLE PERSON</label>
              <input
                type="text"
                className="form-input"
                value={formData['RESPONSIBLE PERSON']}
                onChange={(e) => handleInputChange('RESPONSIBLE PERSON', e.target.value)}
                placeholder="Sorumlu kişi adını girin"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">TELEPHONE</label>
              <input
                type="tel"
                className="form-input"
                value={formData['TELEPHONE']}
                onChange={(e) => handleInputChange('TELEPHONE', e.target.value)}
                placeholder="Telefon numarasını girin"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">EMAIL</label>
              <input
                type="email"
                className="form-input"
                value={formData['EMAIL']}
                onChange={(e) => handleInputChange('EMAIL', e.target.value)}
                placeholder="E-mail adresini girin"
                required
              />
            </div>
          </div>
        </div>

        {/* Recipient Information */}
        <div className="form-section">
          <h3 className="section-title">Recipient Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">RECIPIENT Şirket Adı</label>
              <input
                type="text"
                className="form-input"
                value={formData['RECIPIENT Şirket Adı']}
                onChange={(e) => handleInputChange('RECIPIENT Şirket Adı', e.target.value)}
                placeholder="Alıcı şirket adını girin"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">RECIPIENT Adres</label>
              <textarea
                className="form-textarea"
                value={formData['RECIPIENT Adres']}
                onChange={(e) => handleInputChange('RECIPIENT Adres', e.target.value)}
                placeholder="Alıcı adresini girin"
                rows="3"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">RECIPIENT İlçe İl Ülke</label>
              <input
                type="text"
                className="form-input"
                value={formData['RECIPIENT İlçe İl Ülke']}
                onChange={(e) => handleInputChange('RECIPIENT İlçe İl Ülke', e.target.value)}
                placeholder="Alıcı İlçe, İl, Ülke bilgisini girin"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">RECIPIENT Vat</label>
              <input
                type="text"
                className="form-input"
                value={formData['RECIPIENT Vat']}
                onChange={(e) => handleInputChange('RECIPIENT Vat', e.target.value)}
                placeholder="Vergi numarasını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">RECIPIENT Sorumlu Kişi</label>
              <input
                type="text"
                className="form-input"
                value={formData['RECIPIENT Sorumlu Kişi']}
                onChange={(e) => handleInputChange('RECIPIENT Sorumlu Kişi', e.target.value)}
                placeholder="Alıcı sorumlu kişi adını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">RECIPIENT Telefon</label>
              <input
                type="text"
                className="form-input"
                value={formData['RECIPIENT Telefon']}
                onChange={(e) => handleInputChange('RECIPIENT Telefon', e.target.value)}
                placeholder="Alıcı telefon numarasını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">RECIPIENT Email</label>
              <input
                type="email"
                className="form-input"
                value={formData['RECIPIENT Email']}
                onChange={(e) => handleInputChange('RECIPIENT Email', e.target.value)}
                placeholder="Alıcı e-mail adresini girin"
              />
            </div>
          </div>
        </div>

        {/* Delivery Address */}
        <div className="form-section">
          <h3 className="section-title">Delivery Address</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">DELIVERY ADDRESS Şirket Adı</label>
              <input
                type="text"
                className="form-input"
                value={formData['DELIVERY ADDRESS Şirket Adı']}
                onChange={(e) => handleInputChange('DELIVERY ADDRESS Şirket Adı', e.target.value)}
                placeholder="Teslimat şirket adını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">DELIVERY ADDRESS Adres</label>
              <textarea
                className="form-textarea"
                value={formData['DELIVERY ADDRESS Adres']}
                onChange={(e) => handleInputChange('DELIVERY ADDRESS Adres', e.target.value)}
                placeholder="Teslimat adresini girin"
                rows="3"
              />
            </div>
            <div className="form-group">
              <label className="form-label">DELIVERY ADDRESS İlçe İl Ülke</label>
              <input
                type="text"
                className="form-input"
                value={formData['DELIVERY ADDRESS İlçe İl Ülke']}
                onChange={(e) => handleInputChange('DELIVERY ADDRESS İlçe İl Ülke', e.target.value)}
                placeholder="Teslimat İlçe, İl, Ülke bilgisini girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">DELIVERY ADDRESS Vat</label>
              <input
                type="text"
                className="form-input"
                value={formData['DELIVERY ADDRESS Vat']}
                onChange={(e) => handleInputChange('DELIVERY ADDRESS Vat', e.target.value)}
                placeholder="Teslimat vergi numarasını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">DELIVERY ADDRESS Sorumlu Kişi</label>
              <input
                type="text"
                className="form-input"
                value={formData['DELIVERY ADDRESS Sorumlu Kişi']}
                onChange={(e) => handleInputChange('DELIVERY ADDRESS Sorumlu Kişi', e.target.value)}
                placeholder="Teslimat sorumlu kişi adını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">DELIVERY ADDRESS Telefon</label>
              <input
                type="text"
                className="form-input"
                value={formData['DELIVERY ADDRESS Telefon']}
                onChange={(e) => handleInputChange('DELIVERY ADDRESS Telefon', e.target.value)}
                placeholder="Teslimat telefon numarasını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">DELIVERY ADDRESS Email</label>
              <input
                type="email"
                className="form-input"
                value={formData['DELIVERY ADDRESS Email']}
                onChange={(e) => handleInputChange('DELIVERY ADDRESS Email', e.target.value)}
                placeholder="Teslimat e-mail adresini girin"
              />
            </div>
          </div>
        </div>

        {/* Sipariş Ürünleri */}
        <div className="form-section">
          <div className="goods-header">
            <h3 className="section-title">SİPARİŞ ÜRÜNLERİ</h3>
            <button
              type="button"
              className="btn btn-add-goods"
              onClick={addGoods}
            >
              + Yeni Ürün Ekle
            </button>
          </div>

          {goods.map((item, index) => (
            <div key={item.id} className="goods-item">
              <div className="goods-item-header">
                <h4 className="goods-item-title">Ürün #{index + 1}</h4>
                {goods.length > 1 && (
                  <button
                    type="button"
                    className="btn btn-remove-goods"
                    onClick={() => removeGoods(item.id)}
                  >
                    × Sil
                  </button>
                )}
              </div>

              <div className="goods-container">
                <div className="goods-grid-row">
                  <div className="form-group">
                    <label className="form-label">ARTIKEL NUMARASI</label>
                    <input
                      type="text"
                      className="form-input"
                      value={item['ARTIKEL NUMARASI']}
                      onChange={(e) => handleGoodsChange(item.id, 'ARTIKEL NUMARASI', e.target.value)}
                      placeholder="Artikel numarası"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">GRAMAJ / EN</label>
                    <input
                      type="text"
                      className="form-input"
                      value={item['GRAMAJ / EN']}
                      onChange={(e) => handleGoodsChange(item.id, 'GRAMAJ / EN', e.target.value)}
                      placeholder="Gramaj / En"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">KOMPOZISYON</label>
                    <input
                      type="text"
                      className="form-input"
                      value={item['KOMPOZISYON']}
                      onChange={(e) => handleGoodsChange(item.id, 'KOMPOZISYON', e.target.value)}
                      placeholder="Kompozisyon"
                    />
                  </div>
                </div>

                <div className="goods-grid-row">
                  <div className="form-group">
                    <label className="form-label">SEZON</label>
                    <input
                      type="text"
                      className="form-input"
                      value={item['SEZON']}
                      onChange={(e) => handleGoodsChange(item.id, 'SEZON', e.target.value)}
                      placeholder="Sezon"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">TERMIN ISLEM</label>
                    <input
                      type="text"
                      className="form-input"
                      value={item['TERMIN ISLEM']}
                      onChange={(e) => handleGoodsChange(item.id, 'TERMIN ISLEM', e.target.value)}
                      placeholder="Termin işlem"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">ADET (METRE)</label>
                    <input
                      type="number"
                      className="form-input"
                      value={item['ADET (METRE)']}
                      onChange={(e) => handleGoodsChange(item.id, 'ADET (METRE)', e.target.value)}
                      placeholder="Adet (metre)"
                      step="0.01"
                    />
                  </div>
                </div>

                <div className="goods-grid-row">
                  <div className="form-group">
                    <label className="form-label">FIYAT</label>
                    <input
                      type="text"
                      className="form-input"
                      value={item['FIYAT']}
                      onChange={(e) => handleGoodsChange(item.id, 'FIYAT', e.target.value)}
                      placeholder="Birim fiyat (USD/EUR/TRY) Belirtiniz"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">AMOUNT</label>
                    <input
                      type="text"
                      className="form-input"
                      value={item['AMOUNT']}
                      onChange={(e) => handleGoodsChange(item.id, 'AMOUNT', e.target.value)}
                      placeholder="Toplam tutar (otom. hesaplanır)"
                      readOnly
                      style={{ backgroundColor: '#f8f9fa', cursor: 'default' }}
                    />
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Financial Information */}
        <div className="form-section">
          <h3 className="section-title">Financial Information</h3>
          <div className="form-grid">
            <div className="form-group checkbox-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={formData['Kur Bilgisi Enabled']}
                  onChange={(e) => handleInputChange('Kur Bilgisi Enabled', e.target.checked)}
                />
                <span className="checkmark"></span>
                Kur Bilgisi Ekle
              </label>
              {formData['Kur Bilgisi Enabled'] && (
                <input
                  type="text"
                  className="form-input"
                  value={formData['Kur Bilgisi']}
                  onChange={(e) => handleInputChange('Kur Bilgisi', e.target.value)}
                  placeholder="Kur bilgisini girin (örn: 1 USD = 30.50 TRY)"
                />
              )}
            </div>

            <div className="form-group checkbox-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={formData['KDV Ekle Enabled']}
                  onChange={(e) => handleInputChange('KDV Ekle Enabled', e.target.checked)}
                />
                <span className="checkmark"></span>
                KDV Ekle
              </label>
              {formData['KDV Ekle Enabled'] && (
                <input
                  type="number"
                  className="form-input"
                  value={formData['KDV']}
                  onChange={(e) => handleInputChange('KDV', e.target.value)}
                  placeholder="KDV oranını girin (örn: 20)"
                  step="0.01"
                />
              )}
            </div>

            <div className="form-group">
              <label className="form-label">Banka Bilgileri</label>
              <textarea
                className="form-textarea"
                value={formData['Banka Bilgileri']}
                onChange={(e) => handleInputChange('Banka Bilgileri', e.target.value)}
                placeholder="Banka bilgileri otomatik doldurulur veya manuel girebilirsiniz"
                rows="4"
                readOnly
                style={{ backgroundColor: '#f8f9fa' }}
              />
            </div>
          </div>
        </div>

        {/* Payment & Shipping Details */}
        <div className="form-section">
          <h3 className="section-title">Payment & Shipping Details</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">Payment Terms</label>
              <input
                type="text"
                className="form-input"
                value={formData['Payment Terms']}
                onChange={(e) => handleInputChange('Payment Terms', e.target.value)}
                placeholder="Ödeme koşullarını girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">Transport Type</label>
              <input
                type="text"
                className="form-input"
                value={formData['Transport Type']}
                onChange={(e) => handleInputChange('Transport Type', e.target.value)}
                placeholder="Nakliye türünü girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">Country of Origin</label>
              <input
                type="text"
                className="form-input"
                value={formData['Country of Origin']}
                onChange={(e) => handleInputChange('Country of Origin', e.target.value)}
                placeholder="Menşe ülkesini girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">Gross Weight</label>
              <input
                type="text"
                className="form-input"
                value={formData['Gross Weight']}
                onChange={(e) => handleInputChange('Gross Weight', e.target.value)}
                placeholder="Brüt ağırlığı girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">Net Weight</label>
              <input
                type="text"
                className="form-input"
                value={formData['Net Weight']}
                onChange={(e) => handleInputChange('Net Weight', e.target.value)}
                placeholder="Net ağırlığı girin"
              />
            </div>
            <div className="form-group">
              <label className="form-label">Rolls</label>
              <input
                type="text"
                className="form-input"
                value={formData['Rolls']}
                onChange={(e) => handleInputChange('Rolls', e.target.value)}
                placeholder="Top sayısını girin"
              />
            </div>
          </div>
        </div>

        {/* Total Amount */}
        <div className="form-section">
          <h3 className="section-title">Total Amount</h3>
          <div className="total-container">
            <div className="total-row">
              <span className="total-label">Ara Toplam:</span>
              <span className="total-value">{calculateTotal()}</span>
            </div>
            {formData['KDV Ekle Enabled'] && (
              <div className="total-row">
                <span className="total-label">KDV ({formData['KDV']}%):</span>
                <span className="total-value">
                  {((parseFloat(calculateTotal()) * (parseFloat(formData['KDV']) || 0)) / 100).toFixed(2)}
                </span>
              </div>
            )}
            <div className="total-row total-final">
              <span className="total-label">TOPLAM:</span>
              <span className="total-value">{calculateTotalWithVAT()}</span>
            </div>
          </div>
        </div>

        <div className="form-actions">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => window.history.back()}
          >
            Geri Dön
          </button>
          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? 'PDF Oluşturuluyor...' : 'Sipariş PDF Oluştur'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default SiparisForm;
