import React, { useState } from 'react';
import { generatePDF } from '../api';
import './FabricTechnicalForm.css';

const FabricTechnicalForm = () => {
  const [formData, setFormData] = useState({
    'ARTICLE CODE': '',
    'COMPOSITION': '',
    'WEIGHT': '',
    'WIDTH / CUTABLE WIDTH': '',
    'CERTIFICATION': '',
    'CONSTRUCTION': '',
    'FINISH': '',
    'COLOUR': '',
    'JACQUARD PATTERN NAME': '',
    'MADE IN': '',
    'SHRINKAGE IN WARP': '',
    'SHRINKAGE IN WEFT': '',
    'NOTE_1': '',
    'NOTE_2': '',
    'NOTE_3': ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError(''); // Yeni input girildiğinde hata mesajını temizle
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      console.log('Sending fabric technical data to backend:', formData);
      console.log('Notes specifically:', {
        NOTE_1: formData['NOTE_1'],
        NOTE_2: formData['NOTE_2'],
        NOTE_3: formData['NOTE_3']
      });

      const pdfBlob = await generatePDF('FABRIC_TECHNICAL_SHEET', formData);

      // PDF'i indirmek için blob URL oluştur
      const url = window.URL.createObjectURL(new Blob([pdfBlob], { type: 'application/pdf' }));
      const link = document.createElement('a');
      link.href = url;
      link.download = `TUANA_FABRIC_TECHNICAL_SHEET_${Date.now()}.pdf`;
      link.style.display = 'none';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      setSuccess('PDF başarıyla oluşturuldu ve indirildi!');

    } catch (error) {
      console.error('PDF oluşturma hatası:', error);
      setError(`PDF oluşturulamadı: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFormData({
      'ARTICLE CODE': '',
      'COMPOSITION': '',
      'WEIGHT': '',
      'WIDTH / CUTABLE WIDTH': '',
      'CERTIFICATION': '',
      'CONSTRUCTION': '',
      'FINISH': '',
      'COLOUR': '',
      'JACQUARD PATTERN NAME': '',
      'MADE IN': '',
      'SHRINKAGE IN WARP': '',
      'SHRINKAGE IN WEFT': '',
      'NOTE_1': '',
      'NOTE_2': '',
      'NOTE_3': ''
    });
    setError('');
    setSuccess('');
  };

  return (
    <div className="fabric-form-container">
      <div className="fabric-form-header">
        <h2>TUANA TEKSTİL - Teknik Föy</h2>
        <p>Kumaş teknik bilgi formunu doldurun ve PDF olarak indirin</p>
      </div>

      {error && (
        <div className="alert alert-error">
          <strong>Hata:</strong> {error}
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          <strong>Başarılı:</strong> {success}
        </div>
      )}

      <form onSubmit={handleSubmit} className="fabric-form">
        {/* Teknik Bilgiler Bölümü */}
        <div className="form-section">
          <h3 className="section-title">Teknik Bilgiler</h3>
          <div className="form-grid">
            {Object.keys(formData)
              .filter(fieldName => !fieldName.startsWith('NOTE_'))
              .map((fieldName) => (
                <div key={fieldName} className="form-group">
                  <label htmlFor={fieldName} className="form-label">
                    {fieldName}:
                  </label>
                  <input
                    type="text"
                    id={fieldName}
                    name={fieldName}
                    value={formData[fieldName]}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder={`${fieldName} giriniz...`}
                    disabled={loading}
                  />
                </div>
              ))}
          </div>
        </div>

        {/* Notes Bölümü */}
        <div className="form-section">
          <h3 className="section-title">Notlar (İsteğe Bağlı)</h3>
          <div className="notes-grid">
            {['NOTE_1', 'NOTE_2', 'NOTE_3'].map((noteField, index) => (
              <div key={noteField} className="form-group">
                <label htmlFor={noteField} className="form-label">
                  {index + 1}. Not:
                </label>
                <textarea
                  id={noteField}
                  name={noteField}
                  value={formData[noteField]}
                  onChange={handleInputChange}
                  className="form-textarea"
                  placeholder={`${index + 1}. notu giriniz... (opsiyonel)`}
                  disabled={loading}
                  rows="3"
                />
              </div>
            ))}
          </div>
        </div>

        <div className="form-actions">
          <button 
            type="button" 
            onClick={handleReset}
            className="btn btn-secondary"
            disabled={loading}
          >
            Temizle
          </button>

          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                PDF Oluşturuluyor...
              </>
            ) : (
              'PDF Oluştur ve İndir'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default FabricTechnicalForm;
