import React, { useState } from 'react';
import { generatePDF } from '../api';
import '../css/FabricTechnicalForm.css';

const FabricTechnicalForm = () => {
  const [formData, setFormData] = useState({
    'ARTICLE CODE': '',
    'CUSTOM TARIFF CODE': '',
    'COMPOSITION': '',
    'WEAW TYPE': '',
    'WEIGHT': '',
    'WIDTH / CUTABLE WIDTH': '',
    'CERTIFICATION': '',
    'CONSTRUCTION': '',
    'FINISH': '',
    'COLOUR': '',
    'JACQUARD PATTERN NAME': '',
    'MADE IN': '',
    'SHRINKAGE IN WARP': '',
    'SHRINKAGE IN WEFT': '',
    'NOTE_1': '',
    'NOTE_2': '',
    'NOTE_3': '',
    'ISSUED BY': '',
    'RESPONSIBLE TECHNICIAN': '',
    'CARE_INSTRUCTIONS': []
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Yıkama talimatları fotoğrafları listesi
  const careInstructionsList = [
    { id: 'wash_30', name: 'Yıkama 30°C', image: 'wash_30.png' },
    { id: 'wash_40', name: 'Yıkama 40°C', image: 'wash_40.png' },
    { id: 'wash_60', name: 'Yıkama 60°C', image: 'wash_60.png' },
    { id: 'wash_cold', name: 'Soğuk Yıkama', image: 'wash_cold.png' },
    { id: 'hand_wash', name: 'El Yıkama', image: 'hand_wash.png' },
    { id: 'no_wash', name: 'Yıkama Yapılmaz', image: 'no_wash.png' },
    { id: 'bleach_allowed', name: 'Ağartma Yapılabilir', image: 'bleach_allowed.png' },
    { id: 'no_bleach', name: 'Ağartma Yapılmaz', image: 'no_bleach.png' },
    { id: 'tumble_dry_low', name: 'Düşük İsıda Kurutma', image: 'tumble_dry_low.png' },
    { id: 'tumble_dry_medium', name: 'Orta İsıda Kurutma', image: 'tumble_dry_medium.png' },
    { id: 'tumble_dry_high', name: 'Yüksek İsıda Kurutma', image: 'tumble_dry_high.png' },
    { id: 'no_tumble_dry', name: 'Makine Kurutma Yapılmaz', image: 'no_tumble_dry.png' },
    { id: 'line_dry', name: 'Asarak Kurutma', image: 'line_dry.png' },
    { id: 'iron_low', name: 'Düşük İsıda Ütüleme', image: 'iron_low.png' },
    { id: 'iron_medium', name: 'Orta İsıda Ütüleme', image: 'iron_medium.png' },
    { id: 'iron_high', name: 'Yüksek İsıda Ütüleme', image: 'iron_high.png' },
    { id: 'no_iron', name: 'Ütüleme Yapılmaz', image: 'no_iron.png' },
    { id: 'dry_clean', name: 'Kuru Temizleme', image: 'dry_clean.png' },
    { id: 'no_dry_clean', name: 'Kuru Temizleme Yapılmaz', image: 'no_dry_clean.png' }
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    console.log(`Input changed: ${name} = "${value}"`);
    setFormData(prev => {
      const updated = {
        ...prev,
        [name]: value
      };
      console.log('Updated form data:', updated);
      return updated;
    });
    setError('');
  };

  // Yıkama talimatları seçimi için fonksiyon
  const handleCareInstructionToggle = (instructionId) => {
    setFormData(prev => {
      const currentInstructions = prev['CARE_INSTRUCTIONS'] || [];
      const isSelected = currentInstructions.includes(instructionId);

      const updatedInstructions = isSelected
        ? currentInstructions.filter(id => id !== instructionId)
        : [...currentInstructions, instructionId];

      console.log('Updated care instructions:', updatedInstructions);

      return {
        ...prev,
        'CARE_INSTRUCTIONS': updatedInstructions
      };
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      console.log('Sending fabric technical data to backend:', formData);
      console.log('Notes specifically:', {
        NOTE_1: formData['NOTE_1'],
        NOTE_2: formData['NOTE_2'],
        NOTE_3: formData['NOTE_3']
      });

      const pdfBlob = await generatePDF(formData, 'fabric-technical');

      const url = window.URL.createObjectURL(new Blob([pdfBlob], { type: 'application/pdf' }));
      const link = document.createElement('a');
      link.href = url;

      // Dosya adını ARTICLE CODE ile oluştur
      const articleCode = formData['ARTICLE CODE'] || 'Unknown-Article';
      // Dosya adı için güvenli karakterler kullan
      const safeArticleCode = articleCode.replace(/[^a-zA-Z0-9-_\s]/g, '').replace(/\s+/g, '-');
      link.download = `${safeArticleCode}_TUANA_FABRIC_TECHNICAL_SHEET.pdf`;

      link.style.display = 'none';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      setSuccess('PDF başarıyla oluşturuldu ve indirildi!');

    } catch (error) {
      console.error('PDF oluşturma hatası:', error);
      setError(`PDF oluşturulamadı: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFormData({
      'ARTICLE CODE': '',
      'CUSTOM TARIFF CODE': '',
      'COMPOSITION': '',
      'WEAW TYPE': '',
      'WEIGHT': '',
      'WIDTH / CUTABLE WIDTH': '',
      'CERTIFICATION': '',
      'CONSTRUCTION': '',
      'FINISH': '',
      'COLOUR': '',
      'JACQUARD PATTERN NAME': '',
      'MADE IN': '',
      'SHRINKAGE IN WARP': '',
      'SHRINKAGE IN WEFT': '',
      'NOTE_1': '',
      'NOTE_2': '',
      'NOTE_3': '',
      'ISSUED BY': '',
      'RESPONSIBLE TECHNICIAN': ''
    });
    setError('');
    setSuccess('');
  };

  return (
    <div className="fabric-form-container">
      <div className="fabric-form-header">
        <h2>TUANA TEKSTİL - Teknik Föy</h2>
        <p>Kumaş teknik bilgi formunu doldurun ve PDF olarak indirin</p>
      </div>

      {error && (
        <div className="alert alert-error">
          <strong>Hata:</strong> {error}
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          <strong>Başarılı:</strong> {success}
        </div>
      )}

      <form onSubmit={handleSubmit} className="fabric-form">
        <div className="form-section">
          <h3 className="section-title">Teknik Bilgiler</h3>
          <div className="form-grid">
            {Object.keys(formData)
              .filter(fieldName => !fieldName.startsWith('NOTE_'))
              .map((fieldName) => {
                // ISSUED BY ve RESPONSIBLE TECHNICIAN alanlarını ayrı bölümde gösteriyoruz, burada gösterme
                if (fieldName === 'ISSUED BY' || fieldName === 'RESPONSIBLE TECHNICIAN') {
                  return null;
                }

                return (
                <div key={fieldName} className="form-group">
                  <label htmlFor={fieldName} className="form-label">
                    {fieldName}:
                  </label>

                  {fieldName === 'WEAW TYPE' ? (
                    <select
                      id={fieldName}
                      name={fieldName}
                      value={formData[fieldName]}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={loading}
                    >
                      <option value="">Seçiniz...</option>
                      <option value="POPELINE">POPELINE</option>
                      <option value="PLAIN WEAVE">PLAIN WEAVE</option>
                      <option value="1X1">1X1</option>
                      <option value="TWILL 2/1">TWILL 2/1</option>
                      <option value="TWILL 2/1 S">TWILL 2/1 S</option>
                      <option value="TWILL 2/2">TWILL 2/2</option>
                      <option value="TWILL 3/1">TWILL 3/1</option>
                      <option value="TWILL 3/1 S">TWILL 3/1 S</option>
                      <option value="TWILL 4/1">TWILL 4/1</option>
                      <option value="SATIN">SATIN</option>
                      <option value="JACQUARD">JACQUARD</option>
                      <option value="HERRINGBONE">HERRINGBONE</option>
                      <option value="CREPE">CREPE</option>
                      <option value="DOBBY">DOBBY</option>
                      <option value="CANVAS">CANVAS</option>
                      <option value="BULL">BULL</option>
                      <option value="CHIFFON">CHIFFON</option>
                    </select>
                  ) : fieldName === 'FINISH' ? (
                    <select
                      id={fieldName}
                      name={fieldName}
                      value={formData[fieldName]}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={loading}
                    >
                      <option value="">Seçiniz...</option>
                      <option value="PFD">PFD</option>
                      <option value="DYED">DYED</option>
                      <option value="PIECE DYED">PIECE DYED</option>
                      <option value="REACTIVE DYED">REACTIVE DYED</option>
                      <option value="ENZYMATIC FINISH">ENZYMATIC FINISH</option>
                      <option value="COATED">COATED</option>
                      <option value="AERO">AERO</option>
                      <option value="NYLON TOUCH">NYLON TOUCH</option>
                      <option value="ANTIBACTERIAL">ANTIBACTERIAL</option>
                      <option value="SANFORIZATION">SANFORIZATION</option>
                      <option value="WATER REPELLENT">WATER REPELLENT</option>
                      <option value="WIND REPELLENT">WIND REPELLENT</option>
                      <option value="BRUSHED">BRUSHED</option>
                    </select>
                  ) : fieldName === 'CERTIFICATION' ? (
                    <select
                      id={fieldName}
                      name={fieldName}
                      value={formData[fieldName]}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={loading}
                    >
                      <option value="">Seçiniz...</option>
                      <option value="GOTS">GOTS</option>
                      <option value="GRS">GRS</option>
                      <option value="OCS">OCS</option>
                      <option value="RCS">RCS</option>
                      <option value="EUROPEAN FLAX">EUROPEAN FLAX</option>
                      <option value="ECOVERO">ECOVERO</option>
                      <option value="LENZING">LENZING</option>
                    </select>
                  ) : (
                    <input
                      type="text"
                      id={fieldName}
                      name={fieldName}
                      value={formData[fieldName]}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder={`${fieldName} giriniz...`}
                      disabled={loading}
                    />
                  )}
                </div>
                );
              })}
          </div>
        </div>

        <div className="form-section">
          <h3 className="section-title">Notlar (İsteğe Bağlı)</h3>
          <div className="notes-grid">
            {['NOTE_1', 'NOTE_2', 'NOTE_3'].map((noteField, index) => (
              <div key={noteField} className="form-group">
                <label htmlFor={noteField} className="form-label">
                  {index + 1}. Not:
                </label>
                <textarea
                  id={noteField}
                  name={noteField}
                  value={formData[noteField]}
                  onChange={handleInputChange}
                  className="form-textarea"
                  placeholder={`${index + 1}. notu giriniz... (opsiyonel)`}
                  disabled={loading}
                  rows="3"
                />
              </div>
            ))}
          </div>
        </div>

        <div className="form-section">
          <h3 className="section-title">İmza Bilgileri</h3>
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="ISSUED BY" className="form-label">
                ISSUED BY:
              </label>
              <input
                type="text"
                id="ISSUED BY"
                name="ISSUED BY"
                value={formData['ISSUED BY']}
                onChange={handleInputChange}
                className="form-input"
                placeholder="İmzalayan kişi giriniz..."
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label htmlFor="RESPONSIBLE TECHNICIAN" className="form-label">
                RESPONSIBLE TECHNICIAN:
              </label>
              <input
                type="text"
                id="RESPONSIBLE TECHNICIAN"
                name="RESPONSIBLE TECHNICIAN"
                value={formData['RESPONSIBLE TECHNICIAN']}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Sorumlu teknisyen giriniz..."
                disabled={loading}
              />
            </div>
          </div>
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={handleReset}
            className="btn btn-secondary"
            disabled={loading}
          >
            Temizle
          </button>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                PDF Oluşturuluyor...
              </>
            ) : (
              'PDF Oluştur ve İndir'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default FabricTechnicalForm;
