// src/api.js
import axios from 'axios';

export const generatePDF = async (docType, formData) => {
  const res = await axios.post(
    'http://localhost:5000/api/pdf/generate',
    { docType, formData },
    { responseType: 'blob' } // PDF dosyası dönecek
  );
  return res.data;
};
